const emailInput=document.getElementById("email"),rememberMeCheckbox=document.getElementById("remember-me");function setcookie(){if(!0===rememberMeCheckbox.checked){const e=emailInput.value;document.cookie="user="+e+";path=http://localhost:3000"}}function getCookieData(){console.log(document.cookie);const e=getCookie("user");emailInput.value=e}function getCookie(e){const o=e+"=",t=decodeURIComponent(document.cookie).split(";");for(var n=0;n<t.length;n++){for(var c=t[n];""===c.charAt(0);)c=c.substring(1);if(0===c.indexOf(o))return c.substring(o.length,c.length)}return""}